    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% if app.user %}
        <div class=\"mb-3\">
            You are logged in as {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
        </div>
    {% endif %}

    <div class=\"card\" style=\"width: 80%;margin: auto\">
\t\t<div class=\"card-body\">
\t\t\t<h5 class=\"card-title\">Connexion</h5>

\t\t\t<label for=\"inputPseudo\">Pseudo</label>
\t\t\t<input type=\"text\" value=\"{{ last_username }}\" name=\"username\" id=\"inputUsername\" class=\"form-control\" required>
\t\t\t<label for=\"inputPassword\">Password</label>
\t\t\t<input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" required>
\t\t\t\t\t
\t\t\t<input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">

\t\t\t<input type=\"submit\" value=\"Valider\" class=\"btn btn-primary\">
\t\t</div>
\t</div>

</form>
{% endblock %}
